    private void tbHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_tbHitungActionPerformed
        if(tbBB.getText().equals("")||tbTinggi.getText().equals("")||tbNama.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Kotak isian belum diisi sepenuhnya");
        }else if(rbL.isSelected()||rbP.isSelected()){
            try{
                proses();
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, "Pilih jenis kelamin anda");
            }
        }
        else{JOptionPane.showMessageDialog(null, "Jenis Kelamin");}
    }//GEN-LAST:event_tbHitungActionPerformed

