    private void proses() {
    Double hl = null;
    String temp;
    Double ti = Double.valueOf(tbTinggi.getText());
    Double bt = Double.valueOf(tbBB.getText());
    if(rbP.isSelected()){
        hl = (ti-104)*1;
    }else if(rbL.isSelected()){
        hl = (ti-103)*1;
    }
    temp = Double.toString(hl.intValue());
    
    if(hl<bt){
        tbIdeal.setText(temp);
        tbHasil.setText("Maaf "+tbNama.getText()+" ,Berat badan anda diambang batas normal");
        tbSaran.setText("Puasalah");
    }else if(hl>bt){
        tbIdeal.setText(temp);
        tbHasil.setText("Maaf "+tbNama.getText()+" ,Anda seperti anak kos");
        tbSaran.setText("Berbukalah");
    }else{
        tbIdeal.setText(temp);
        tbHasil.setText("Bagus "+tbNama.getText()+" ,Berat badan anda ideal coy");
        tbSaran.setText("Mantab Jiwa");
    }
    
    }
